	//保存文档入数据库  uri为gaSavePath，gaSavePath在新建文件和打开文件的时候指定。
	function gasaveit(uri,SearchPageName){
		var gaeditor = document.getElementsByName("gaeditor")[0];		
		dt=gaeditor.GetAsciiData();
	    var XmlHttp = new ActiveXObject("Microsoft.XMLHTTP");
		if(uri==null){			
			uri = gaSavePath;
		}		
		if(SearchPageName==null)
			SearchPageName="../../../eformsys/gaeditor/savega.jsp";
		var xmlheader="<?xml version='1.0'?>";
		var XmlSend=xmlheader+"<root><url>"+uri+"</url><data>"+dt+"</data></root>";
		XmlHttp.open("POST",SearchPageName,false);
		XmlHttp.setRequestHeader("Content-Type","text/xml;");
	    XmlHttp.send(XmlSend);  
	    var status = XmlHttp.responseText;
	    if(status.indexOf("yes")!=-1){
	    	alert("保存成功！");
	    }else{
	    	alert("保存失败!");
	    }
	}
    
    //载入文档 
	function galoadit(gaeditor_insid){
	 	var gaeditor = document.getElementsByName("gaeditor")[0];
	 	var ctrlId = "gaEditor1";	 	
	 	if(IsSpace(gaeditor_insid)){
	 		alert("galoadit() 函数的gaeditor_insid参数没有传递！");
	 		return;
	 	}
	 	var fileName = gaeditor_insid + gaFileType;
	 	
	 	var XmlHttp = new ActiveXObject("Microsoft.XMLHTTP");
	 	var SearchPageName= gaOpenPathPre + "/" + fileName;
		XmlHttp.open("POST",SearchPageName,false)
		XmlHttp.setRequestHeader("Content-Type","text/xml")
	    XmlHttp.send("");
		dt=XmlHttp.responseText;
		//alert("dt:"+dt);
		if(dt!=""){
			gaeditor.PutAsciiData(dt);
			gaSavePath = "/" + fileName;   //保存的文件名
		}else{
			alert(fileName+"文件不存在!");
		}
	}
	
	//新建文档,新建模板
	function gaNewFile(gaeditor_insid){
	    clearit();
	    var gaeditor = document.getElementsByName("gaeditor")[0];
	 	var ctrlId = "gaEditor1";	 	
	 	if(IsSpace(gaeditor_insid)){
	 		alert("gaNewFile() 函数中的gaeditor_insid不能为空");	 		
	 		return;
	 	}
	 	var fileName = gaeditor_insid + gaFileType;
	    gaSavePath = "/" + fileName;   //保存的文件名
	}
	
	
	//从模板新建文档。
	function gaNewFileFromTemplate(template_insid,gaeditor_insid){
		var gaeditor = document.getElementsByName("gaeditor")[0];
		var ctrlId = "gaEditor1";	 	
	 	if(IsSpace(gaeditor_insid)){
	 		alert("gaNewFileFromTemplate() 函数中的gaeditor_insid不能为空");	 		
	 		return;
	 	}
	 	var template_fileName = template_insid + gaFileType;
	 	var new_fileName = gaeditor_insid + gaFileType;
	 	
	 	var XmlHttp = new ActiveXObject("Microsoft.XMLHTTP");
	 	var SearchPageName=gaOpenPathPre + "/" + template_fileName;
		XmlHttp.open("POST",SearchPageName,false)
		XmlHttp.setRequestHeader("Content-Type","text/xml")
	    XmlHttp.send("");
		dt=XmlHttp.responseText;
		if(dt!=""){	
	    	gaeditor.PutAsciiData(dt);
	    }else{
	    	alert("模板"+template_fileName+"不存在！");
	    }
	    gaSavePath = "/" + new_fileName;  //新建文件的名字,保存时使用该名字。
	}
    
    //清除
