    //禁止新建   
    function noNew(){
    	var gaeditor = document.getElementsByName("gaeditor")[0];			
		gaeditor.RVMenuControl("FileNew",false,false);
	}
	
	//允许新建
	function enNew(){
		var gaeditor = document.getElementsByName("gaeditor")[0];
		gaeditor.RVMenuControl("FileNew",true,true);
	}
	
	//禁止保存
	function noSave(){
		var gaeditor = document.getElementsByName("gaeditor")[0];
		gaeditor.RVMenuControl("FileSave",false,false);
		gaeditor.RVMenuControl("FileSaveAs",false,false);
	}
	
	//允许保存
	function enSave(){
		var gaeditor = document.getElementsByName("gaeditor")[0];
		gaeditor.RVMenuControl("FileSave",true,true);
		gaeditor.RVMenuControl("FileSaveAs",true,true);
	}
	
	//全文只读
	function nocopy(){
		var gaeditor = document.getElementsByName("gaeditor")[0];
		gaeditor.ReadOnly="true"
	}
    
    //显示表格控制菜单   
    function goTable(){
		var t=document.all("textCtrl");
		t.style.display="none"
		var tab=document.all("tableCtrl");
		tab.style.display="block"
	}
	
	//显示全文控制菜单
	function goTxt(){
   		var t=document.all("textCtrl");
  		t.style.display="block"
  		var tab=document.all("tableCtrl");
  		tab.style.display="none"  
	}
    
    //读取表格某行某列的数据 
	function readContent(){
		var gaeditor = document.getElementsByName("gaeditor")[0];
		var t=document.all("tableid").value;
    	var r=document.all("rowid").value;
		var c=document.all("colid").value;    	 	  			
 		alert(gaeditor.GetTableContent(t,r,c));
	}
	
	//向表格某行某列插入数据
	function writeContent(){
		var gaeditor = document.getElementsByName("gaeditor")[0];
		var t=document.all("tableid").value;
    	var r=document.all("rowid").value;
		var c=document.all("colid").value;  
		var to=document.all("toinsert").value;      		
		gaeditor.InsertTableContent(to,t,r,c);
	}
	
	//清空表格某行某列的数据
	function clearContent(){		
		var gaeditor = document.getElementsByName("gaeditor")[0];
		var t=document.all("tableid").value;
    	var r=document.all("rowid").value;
		var c=document.all("colid").value;
		gaeditor.ClearTableCell(t,r,c)
	}
	
	//设置表格某行某列为只读
	function setReadonly(){			
		var gaeditor = document.getElementsByName("gaeditor")[0];
		var t=document.all("tableid").value;
    	var r=document.all("rowid").value;
		var c=document.all("colid").value;
		gaeditor.SetTableCellReadOnly(t,r,c)
	}
    
    //功能不详
    function getnull(e){
		while(e.indexOf("\r\n")>=0){
		  e=e.replace("\r\n","");
		}
		return e; 
	}
	
