public class SubsysManageBean extends XtDbBaseBean{
	
	
	public SubsysManageBean() {
		super("OA_SUBSYS_MANAGE", new String[]{"sys_id"});
		// TODO Auto-generated constructor stub
	}
	private String sys_id;		//系统主键
	private String sys_name;		//系统名称
	private String sys_desc;		//系统描述
	private String sys_url;		//系统访问路径
	private String sys_img_url;		//系统对应图片路径
	private String sys_ispublic;		//是否为公共系统（Y:是，N:否）
	/**
	*<b>Summary: 获取系统主键</b>
	*/
	public String getSys_id() {
		return sys_id;
	}
	/**
	*<b>Summary: 设置系统主键</b>
	*/
	public void setSys_id(String sys_id) {
		this.sys_id = sys_id;
	}
	/**
	*<b>Summary: 获取系统名称</b>
	*/
	public String getSys_name() {
		return sys_name;
	}
	/**
	*<b>Summary: 设置系统名称</b>
	*/
	public void setSys_name(String sys_name) {
		this.sys_name = sys_name;
	}
	/**
	*<b>Summary: 获取系统描述</b>
	*/
	public String getSys_desc() {
		return sys_desc;
	}
	/**
	*<b>Summary: 设置系统描述</b>
	*/
	public void setSys_desc(String sys_desc) {
		this.sys_desc = sys_desc;
	}
	/**
	*<b>Summary: 获取系统访问路径</b>
	*/
	public String getSys_url() {
		return sys_url;
	}
	/**
	*<b>Summary: 设置系统访问路径</b>
	*/
	public void setSys_url(String sys_url) {
		this.sys_url = sys_url;
	}
	/**
	*<b>Summary: 获取系统对应图片路径</b>
	*/
	public String getSys_img_url() {
		return sys_img_url;
	}
	/**
	*<b>Summary: 设置系统对应图片路径</b>
	*/
	public void setSys_img_url(String sys_img_url) {
		this.sys_img_url = sys_img_url;
	}
	/**
	*<b>Summary: 获取是否为公共系统（Y:是，N:否）</b>
	*/
	public String getSys_ispublic() {
		return sys_ispublic;
	}
	/**
	*<b>Summary: 设置是否为公共系统（Y:是，N:否）</b>
	*/
	public void setSys_ispublic(String sys_ispublic) {
		this.sys_ispublic = sys_ispublic;
	}
	
}
